select.execute:

var list = [],
	current,
	request,
	i = 0,
	length = url.length;

for ( ; i < length ; i++ ) {

	list.push ( current = { url: url[ i ] } );

	request = y.rest( url[ i ] ).accept( 'text/plain' ).get();

	if ( request.response ) {

		current.content = request.response;

	} else {

		current.error = request.status;

	}

}

response.object = {
	list: list
};
